class Student extends Component {
    constructor() {
        super();
        this.state = {
        }
    }

    toggleFormDetail = () => {
        this.props.controlFormDetail(true,);
        this.setState({
            isToggle: true
        })
    }
    updateInfoStudent = (id) => {
        this.props.controlFormDetail(true, "UpdateStudent", selectedStudent);
        this.props.inputToFormDetail(id);
    }


    render() {
        let { studentDetailInfo } = this.props;//== studentDetailInfo = this.props.studentDetailInfo
        // console.log(stt);
        return (
            <tr>
                <td>{this.props.stt + 1}</td>
                <td>{studentDetailInfo.studentId}</td>
                <td>{studentDetailInfo.studentName}</td>
                <td>{studentDetailInfo.studentAge}</td>
                <td>{studentDetailInfo.studentGender ? "Nam" : "Nữ"}</td>
                <td>
                    <div className="template-demo">
                        <button
                            type="button"
                            className="btn btn-danger btn-icon-text"
                            onClick={() => this.sendInfoStudent(studentDetailInfo.studentId)}
                        >
                            Xem
                        </button>
                        <button
                            type="button"
                            className="btn btn-warning btn-icon-text"
                            onClick={() => this.updateInfoStudent(studentDetailInfo.studentId)}
                        >
                            Sửa
                        </button>
                        <button
                            type="button"
                            className="btn btn-success btn-icon-text"
                        >
                            Xóa
                        </button>
                    </div>
                </td>
            </tr>
        )
    }
}

export default Student
