import logo from './logo.svg';
import './App.css';
import Control from './components/Control.js'
import ListStudent from './components/ListStudent.js'
import Form from './components/Form.js';
import React, { Component } from 'react';

class App extends Component {
  constructor() {
    super();
    this.state = {
      isToggle: false,
      students: [
        { studenId: "SV001", studentName: "Nguyễn Văn A", studentAge: 20, studentGender: true },
        { studenId: "SV002", studentName: "Nguyễn Văn B", studentAge: 19, studentGender: false },
        { studenId: "SV003", studentName: "Nguyễn Văn C", studentAge: 21, studentGender: true },
      ],
    }
  }
  render() {
    let elementForm;
    if (this.state.isToggle) {
      elementForm = <Form></Form>;
    }
    else {
      elementForm = '';
    }
    return (
      <div className="App">
        <div className="row">
          <div className="col-lg-7 grid-margin stretch-card">
            <div className="card">
              {/* START CONTROL */}
              <Control></Control>
              {/* END CONTROL */}
              {/* START LIST STUDENT */}
              <ListStudent></ListStudent>
              {/* END LIST STUDENT */}
            </div>
          </div>
          {/* START FORM SINH VIEN */}
          {elementForm}
          {/* END FORM SINH VIÊN */}
        </div>
      </div>
    );
  }
}

export default App;
