import React, { Component } from "react";

class Form extends Component {
    constructor() {
        super();
        this.state = {
            studentId: '',
            studentName: '',
            studentAge: '',
            studentGender: false,
            studentDateBirth: '',
            studentPlaceBirth: '',
            studentAddress: '',
        }
    }
    componentWillMount = () => {
        // console.log(this.props.selectedStudent);//First TIme
        let { selectedStudent } = this.props;
        this.setState({
            studentId: selectedStudent.studentId,
            studentName: selectedStudent.studentName,
            studentAge: selectedStudent.studentAge,
            studentGender: selectedStudent.studentGender,
            studentDateBirth: selectedStudent.studentDateBirth,
            studentPlaceBirth: selectedStudent.studentPlaceBirth,
            studentAddress: selectedStudent.studentAddress,
        })
    }

    componentWillReceiveProps = (nextProps) => {
        // console.log(this.props.selectedStudent);//Other time
        let { selectedStudent } = nextProps;
        this.setState({
            studentId: selectedStudent.studentId,
            studentName: selectedStudent.studentName,
            studentAge: selectedStudent.studentAge,
            studentGender: selectedStudent.studentGender,
            studentDateBirth: selectedStudent.studentDateBirth,
            studentPlaceBirth: selectedStudent.studentPlaceBirth,
            studentAddress: selectedStudent.studentAddress,
        })
    }

    handleChange = (event) => {
        let name = event.target.name
        let value = event.target.value
        this.setState({
            [name]: value
        })
    }
    checkValidateNewStudent = () => {
        console.log(this.state);
    }


    checkValidateCurrentId = () => {
        let checkID = true;
        this.props.listStudents.forEach(student => {
            if (student.studentId.toLowerCase() == this.state.studentId.toLowerCase()) {
                checkID = false;
            }
        });
        return checkID
    }

    createNewStudent = (event) => {
        console.log(this.checkValidateNewStudent());
        // console.log(this.props.listStudents);
        let checkID = this.checkValidateCurrentId();
        if (checkID) {
            let newStudent = {
                studentId: this.state.studentId,
                studentName: this.state.studentName,
                studentAge: this.state.studentAge,
                studentGender: this.state.studentGender,
                studentDateBirth: this.state.studentDateBirth,
                studentPlaceBirth: this.state.studentPlaceBirth,
                studentAddress: this.state.studentAddress,
            }
            this.props.controlForm(false)
            this.props.setNewStudent(false, newStudent)
        }
        else {
            window.alert("Mã Sinh viên đã tồn tại, vui lòng thử lại mã khác")
        }
        event.preventDefault()
    }


    updateInfoStudent = (event) => {
        let checkID = this.checkValidateCurrentId();
        if (!checkID) {
            let studentUpdated = {
                studentId: this.state.studentId,
                studentName: this.state.studentName,
                studentAge: this.state.studentAge,
                studentGender: this.state.studentGender,
                studentDateBirth: this.state.studentDateBirth,
                studentPlaceBirth: this.state.studentPlaceBirth,
                studentAddress: this.state.studentAddress,
            }
            this.props.updateInfoStudent(false, studentUpdated)
        }
        else {
            window.alert("Mã sinh viên không được phép thay đổi")
        }
        event.preventDefault()
    }



    hideForm = () => {
        this.props.controlForm(false);
    }



    render() {
        let { actionName } = this.props
        let elementButton = ''
        if (actionName == "CreateStudent") {
            elementButton = <button type="submit" className="btn btn-primary me-2" onClick={this.createNewStudent}>
                Create
            </button>
        }
        else if (actionName == "UpdateStudent") {
            elementButton = <button type="submit" className="btn btn-primary me-2" onClick={this.updateInfoStudent}>
                Update
            </button>
        }


        return (
            <div className="col-5 grid-margin">
                <div className="card">
                    <div className="card-body">
                        <h3 className="card-title">Thông tin sinh viên</h3>
                        <i className="fa-regular fa-circle-xmark fa-xl icon-close" onClick={this.hideForm} />
                        <form className="form-sample">
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Mã sinh viên</label>
                                <div className="col-sm-9">
                                    <input type="text" className="form-control" name="studentId" value={this.state.studentId} onChange={this.handleChange} />
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Tên sinh viên</label>
                                <div className="col-sm-9">
                                    <input type="text" className="form-control" name="studentName" value={this.state.studentName} onChange={this.handleChange} />
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Tuổi</label>
                                <div className="col-sm-9">
                                    <input type={"number"} className="form-control" name="studentAge" value={this.state.studentAge} onChange={this.handleChange} />
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Giới tính</label>
                                <div className="col-sm-9">
                                    <select className="form-control" name="studentGender" value={this.state.studentGender} onChange={this.handleChange}>
                                        <option value={true}>Nam</option>
                                        <option value={false}>Nữ</option>
                                    </select>
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Ngày sinh</label>
                                <div className="col-sm-9">
                                    <input type={"date"} className="form-control" placeholder="dd/mm/yyyy" name="studentDateBirth" value={this.state.studentDateBirth} onChange={this.handleChange} />
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Nơi sinh</label>
                                <div className="col-sm-9">
                                    <select className="form-control" name="studentPlaceBirth" value={this.state.studentPlaceBirth} onChange={this.handleChange}>
                                        <option value={"Hà Nội"}>Hà Nội</option>
                                        <option value={"TP. Hồ Chí Minh"}>TP. Hồ Chí Minh</option>
                                        <option value={"Đà Nẵng"}>Đà Nẵng</option>
                                        <option value={"Quảng Ninh"}>Quảng Ninh</option>
                                    </select>
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-3 col-form-label">Địa chỉ</label>
                                <div className="col-sm-9">
                                    <textarea className="form-control" name="studentAddress" value={this.state.studentAddress} onChange={this.handleChange} />
                                </div>
                            </div>
                            {elementButton}
                        </form>
                    </div>
                </div >
            </div >
        )
    }
}

export default Form
